mapper.select:
SELECT COUNT(1) FROM app_info WHERE 1=1
	
		AND softwareName LIKE CONCAT('%',#{softwareName},'%') 
	
	
		AND status = #{status}
	
	
		AND flatformId = #{flatformId}
	
	
		AND categoryLevel1 = #{categoryLevel1}
	
	
		AND categoryLevel2 = #{categoryLevel2}
	
	
		AND categoryLevel3 = #{categoryLevel3}

SELECT a.id, a.softwareName,a.APKName,a.supportROM,a.softwareSize,a.devId, (SELECT devName FROM dev_user WHERE id = a.devId) AS devName, a.status, (SELECT valueName FROM data_dictionary d WHERE a.status=d.valueId AND d.typeCode='APP_STATUS') AS statusName, a.flatformId, (SELECT valueName FROM data_dictionary d WHERE a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') AS flatformName, a.categoryLevel1, (SELECT categoryName FROM app_category c WHERE c.id=a.categoryLevel1) AS categoryLevel1Name, a.categoryLevel2, (SELECT categoryName FROM app_category c WHERE c.id=a.categoryLevel2) AS categoryLevel2Name, a.categoryLevel3, (SELECT categoryName FROM app_category c WHERE c.id=a.categoryLevel3) AS categoryLevel3Name, a.downloads,a.onSaleDate,a.versionId, (SELECT v.versionNo FROM app_version v WHERE v.id=a.versionId ) AS versionNo FROM app_info a WHERE 1=1
AND a.softwareName LIKE CONCAT('%',#{softwareName},'%')
AND a.status = #{status}
AND a.flatformId = #{flatformId}
AND a.categoryLevel1 = #{categoryLevel1}
AND a.categoryLevel2 = #{categoryLevel2}
AND a.categoryLevel3 = #{categoryLevel3}
ORDER BY a.id DESC limit #{currentPageNo},#{pageSize}
